pragma solidity >=0.5.0; // Version of Solidity - 0.5.0


contract SocialNetwork {
  string public name;
  uint public postCount = 0;
  mapping(uint => Post) public posts;

  struct Post {
    uint id;
    string content;
    uint tipAmount;
    address payable author;
  }

  event PostCreated(
    uint id,
    string content,
    uint tipAmount,
    address payable author
  );

  event PostTipped(
    uint id,
    string content,
    uint tipAmount,
    address payable author
  );

  constructor() public {
    name = "skhiearth";
  }

  function createPost(string memory _content) public {
    require(bytes(_content).length > 0, 'Content cannot be empty');
    postCount++;
    posts[postCount] = Post(postCount, _content, 0, msg.sender);
    emit PostCreated(postCount, _content, 0, msg.sender);
  }

  function tipPost(uint _id) public payable {
    require(_id > 0 && _id <= postCount, 'Id not valid');
    Post memory _post = posts[_id]; // Fetch the post
    address payable _author = _post.author; // Fetch the author
    _author.transfer(msg.value); // Pay the author
    _post.tipAmount = _post.tipAmount + msg.value; // Increment the tip amount for the post
    posts[_id] = _post; // Update the post
    emit PostTipped(postCount, _post.content, _post.tipAmount, _author); // Trigger Event
  }
}
